# Code from here: https://code.google.com/p/oosd-bioinfo/source/browse/python/needleman-wunsch.py?r=ff8b3d2627d2a04e738ff49da11fc402bba02490
def needleman_wunsch_ops(s, w):
    d, A, B = 0, " " + s, " " + w # d is cost of -
    I, J = range(len(A)), range(len(B))
    F = [[0 for j in J] for i in I] # Fill a 2D array with zeroes

    # Initialization
    for i in I:
            F[i][0] = d * i
    for j in J:
            F[0][j] = d * j
            
    # Scoring
    for i in I[1:]:
            for j in J[1:]:
                    Match = F[i-1][j-1] + conf(A[i], B[j])
                    Delete = F[i-1][j] + d
                    Insert = F[i][j-1] + d
                    F[i][j] = max(Match, Insert, Delete)

    # Traceback
    alignmentS, alignmentW = "", ""
    i, j = len(A) - 1, len(B) - 1

    while (i > 0 and j > 0):
            Score = F[i][j]
            ScoreDiag = F[i - 1][j - 1]
            ScoreUp = F[i][j - 1]
            ScoreLeft = F[i - 1][j]
            if (Score == ScoreDiag + conf(A[i], B[j])):
                    alignmentS = A[i] + alignmentS
                    alignmentW = B[j] + alignmentW
                    i -= 1
                    j -= 1
            elif (Score == ScoreLeft + d):
                    alignmentS = A[i] + alignmentS
                    alignmentW = "-" + alignmentW
                    i -= 1
            elif (Score == ScoreUp + d):
                    alignmentS = "-" + alignmentS
                    alignmentW = B[j] + alignmentW
                    j -= 1
            else:
                    print("algorithm error?")
    while (i > 0):
            alignmentS = A[i] + alignmentS
            alignmentW = "-" + alignmentW
            i -= 1
    while (j > 0):
            alignmentS = "-" + alignmentS
            alignmentW = B[j] + alignmentW
            j -= 1

    for i in range(len(alignmentS)):
        if alignmentS[i] != '-' and alignmentW[i] != '-':
            yield ("sub", (alignmentS[i], alignmentW[i]))
        elif alignmentS[i] == '-':
            yield ("del", alignmentW[i])
        else: # alignmentW[i] == '-'
            yield ("ins", alignmentS[i])


# If k exists in d, increment value; otherwise, initialize as 1
def increment_dict(d, k):
    d[k] = 1.0 if k not in d else d[k] + 1.0


# Writes dictionary to output_file_path in the format: op c [c2] value
def write_file(ins, delete, sub, output_file_path):
    print("Writing model........")
    with open(output_file_path, 'wt') as f:
        for k, v in ins.items():
            f.write("INS " + k + " " + str(v) + "\n")
        for k, v in delete.items():
            f.write("DEL " + k + " " + str(v) + "\n")
        for (s1, w1), v in sub.items():
            f.write("SUB " + s1 + " " + w1 + " " + str(v) + "\n")

# Get aligned words
def get_aligned():
    result = []
    directory = './' + sys.argv[1] + '/Aligned/*_aligned.txt'
    files_wcol = glob.glob(directory)

    for file_path in files_wcol:
        print("Getting aligned from: " + file_path)

        with open(file_path,'r', encoding='utf-8') as f:
            for line in f.readlines():
                toks = line.replace("\n", "").split('\t')
                if toks[0] != "null" and toks[1] != "null":
                    result.append((toks[0].replace("@", ""), toks[1].replace("@", "")))

    return result